class SiteJsInterop{constructor(){}static load(){window[SiteJsInterop.name]=new SiteJsInterop}collapseNavMenu(n,t){document.body.classList[n?"add":"remove"](`${t}--opened`)}setupChart(n){this.chart=LightweightCharts.createChart(n,{height:400,grid:{vertLines:{color:"rgba(197, 203, 206, 0.5)"},horzLines:{color:"rgba(197, 203, 206, 0.5)"}},crosshair:{mode:LightweightCharts.CrosshairMode.Normal},rightPriceScale:{visible:!0,borderColor:"rgba(197, 203, 206, 1)"},leftPriceScale:{visible:!0,borderColor:"rgba(197, 203, 206, 1)"},timeScale:{borderColor:"rgba(197, 203, 206, 0.8)"},watermark:{visible:!0,fontSize:24,horzAlign:"center",vertAlign:"center",color:"rgba(171, 71, 188, 0.5)"}});let t;document.body.onresize=()=>{t&&clearTimeout(t),t=setTimeout(()=>this.resizeChart(),100)};this.chartSeriesMap=new Map}setChartData(n,t){if(this.chartSeriesMap.forEach((n,t,i)=>{this.chart.removeSeries(n),i.delete(t)}),this.chart.applyOptions({watermark:{text:n.toUpperCase()}}),t.candle&&Array.isArray(t.candle)){const n=this.chart.addCandlestickSeries({priceScaleId:"left",title:"AAPL"});n.setData(t.candle);this.chartSeriesMap.set("candle",n)}if(t.volume&&t.volume.length){const n=this.chart.addHistogramSeries({priceFormat:{type:"volume"},priceLineVisible:!1,color:"rgba(76, 175, 80, 0.5)",priceScaleId:"",scaleMargins:{top:.85,bottom:0}});n.setData(t.volume);this.chartSeriesMap.set("volume",n)}if(t.overlayLines&&Array.isArray(t.overlayLines))for(let n=0;n<t.overlayLines.length;n++){const i=this.chart.addLineSeries({priceScaleId:"right",lineWidth:1});i.setData(t.overlayLines[n]);this.chartSeriesMap.set(`overlayLine${n}`,i)}if(t.valueLines&&Array.isArray(t.valueLines))for(let n=0;n<t.valueLines.length;n++){const i=this.chart.addLineSeries({priceScaleId:"",scaleMargins:{top:.75,bottom:0},lineWidth:1});i.setData(t.valueLines[n]);this.chartSeriesMap.set(`valueLine${n}`,i)}window.dispatchEvent(new UIEvent("resize"));this.chart.timeScale().fitContent()}resizeChart(){const n=document.getElementById("chart");this.chart.resize(n.offsetWidth,n.offsetHeight)}}SiteJsInterop.load();